namespace Magestore\Customercredit\Block\Adminhtml;

class Customercredit extends \Magento\Backend\Block\Widget\Grid\Container
{
    /**
     * @return void
     */
    protected function _construct()
    {
        $this->_blockGroup = 'Magestore_Customercredit';
        $this->_controller = 'adminhtml_customercredit';
        $this->_headerText = __('Manage Customers Using Credit');
        parent::_construct();
        $this->buttonList->remove('add');
    }
}